INSERT INTO grammar_points (grammar_point, structure, meaning, jlpt_level, example_sentence, example_translation, notes) VALUES
                                                                                                                             ('です', '名词 + です', '是（礼貌体）', 'N5', '私は学生です。', '我是学生。', '用于句尾表示礼貌陈述'),
                                                                                                                             ('ます形', '动词ます形', '动词礼貌形式', 'N5', '食べます。', '吃。', '动词的礼貌表达方式'),
                                                                                                                             ('が', '名词 + が + 动词', '主语标记', 'N5', '猫がいます。', '有猫。', '表示句子的主语'),
                                                                                                                             ('を', '名词 + を + 动词', '宾语标记', 'N5', '本を読みます。', '读书。', '表示动作的直接对象'),
                                                                                                                             ('に', '名词 + に + 动词', '时间/地点标记', 'N5', '学校に行きます。', '去学校。', '表示动作的方向或时间点'),
                                                                                                                             ('て形', '动词て形', '动词连接形式', 'N4', '食べて、飲みます。', '吃了然后喝。', '用于连接多个动作'),
                                                                                                                             ('たい', '动词ます形 + たい', '想要做某事', 'N4', '日本に行きたいです。', '想去日本。', '表达愿望'),
                                                                                                                             ('なければならない', '动词ない形 + ければならない', '必须做某事', 'N3', '勉強しなければならない。', '必须学习。', '表示义务或必要性'),
                                                                                                                             ('かもしれない', '动词/形容词普通形 + かもしれない', '也许', 'N3', '雨が降るかもしれない。', '也许要下雨。', '表示不确定的推测'),
                                                                                                                             ('ばかり', '动词た形 + ばかり', '刚刚', 'N3', '食べたばかりです。', '刚刚吃完。', '表示动作刚完成不久'),
                                                                                                                             ('ように', '动词字典形 + ように', '为了，以便', 'N2', '早く起きられるように、早く寝ます。', '为了能早起，早点睡。', '表示目的'),
                                                                                                                             ('わけではない', '动词/形容词普通形 + わけではない', '并不是', 'N2', '好きなわけではない。', '并不是喜欢。', '表示部分否定'),
                                                                                                                             ('ものだ', '动词普通形 + ものだ', '应该，理应', 'N1', '子供は早く寝るものだ。', '小孩应该早睡。', '表示普遍真理或社会规范'),
                                                                                                                             ('ことから', '动词/形容词普通形 + ことから', '由于，因为', 'N1', '彼はよく知っていることから、専門家だと思う。', '因为他很了解，所以我认为他是专家。', '表示原因或根据'),
                                                                                                                             ('ところを', '动词ている形 + ところを', '正在...的时候', 'N1', '寝ているところを起こされた。', '正在睡觉的时候被叫醒了。', '表示动作进行中被干扰'),
                                                                                                                             ('ても', '动词て形 + も', '即使...也', 'N4', '高くても買います。', '即使贵也买。', '表示让步'),
                                                                                                                             ('ながら', '动词ます形 + ながら', '一边...一边', 'N4', '音楽を聞きながら勉強します。', '一边听音乐一边学习。', '表示两个动作同时进行'),
                                                                                                                             ('そうだ', '动词ます形 + そうだ', '看起来', 'N3', 'おいしそうですね。', '看起来很好吃。', '表示样态'),
                                                                                                                             ('らしい', '动词/形容词普通形 + らしい', '似乎，好像', 'N2', '彼は先生らしい。', '他好像是老师。', '表示有根据的推测'),
                                                                                                                             ('がち', '动词ます形/名词 + がち', '容易，经常', 'N2', '忘れがちです。', '容易忘记。', '表示某种倾向');
